import React from 'react'
import { useLocation } from 'react-router-dom'; // Import useLocation
import useSearchFetching from '../services/search.service';

function Home() {
    const location = useLocation(); // Sử dụng hook useLocation
    const searchQuery = new URLSearchParams(location.search).get('query'); // Lấy giá trị query params từ URL
    const { data, loading } = useSearchFetching(`/search?query=${searchQuery}`);

    return (
        <>
            {loading ? (
                <div>Loading...</div>
            ) : (
                <ul>{data.map(item => <li>{item.title}</li>)}</ul>
            )}
        </>
    );
}

export default Home;
