import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {

    fetch("/api/truyenfull")
      .then((res) => res.json())
      .then((data) => setData(data.message));

  },[])

  return (
        <p>{!data ? "Loading..." : data}</p>
  );
}

export default App;
